const en = require('./en')
const tn = require('./tn')
const ar = require('./ar')
const es = require('./es')

function greet(language) {
    if (language === "english") {
        en();
        return;
    }
    if (language === "tamil") {
        tn();
        return;
    }
    if (language === "arabic") {
        ar();
        return;
    }
    if (language === "spanish") {
        es();
        return;
    }
    console.log("greetings to you, provide lang for personalized greetings")
}

// greet("tamil")

module.exports = {
    greet
}

